<script>
import { ref, computed } from '@vue/reactivity'
import { useStore } from 'vuex'
import { useRouter } from 'vue-router'
import { onMounted } from '@vue/runtime-core'
export default {
  components: {},
  setup(props) {
    const store = useStore()
    const router = useRouter()
    const userLocal = computed(() => store.state.currentUser)
    store.commit('SELECT_PAGE', 'Админ')
    const currentProduct = ref('Combo')
    const handleCurrentProduct = (text, name) => {
      currentProduct.value = text
      store.commit('SELECT_PAGE', name)
    }
    onMounted(() => {
      if (!userLocal.value.admin) {
        router.push('/')
        store.commit('SELECT_PAGE', 'Главная')
      }
      if (userLocal.value.admin) {
        return
      }
    })

    return { store, userLocal, currentProduct, handleCurrentProduct }
  },
}
</script>

